"use client"
import { View, Text, Image, StyleSheet } from "react-native"
import { useAuth } from "@/app/context/AuthContext"

type CommentProps = {
  comment: {
    id: string
    author: {
      id: string
      username: string
      avatar: string
      isAdmin: boolean
    }
    content: string
    createdAt: string
  }
}

export default function Comment({ comment }: CommentProps) {
  const { user } = useAuth()

  const formatDate = (dateString: string) => {
    const date = new Date(dateString)
    return date.toLocaleDateString("es-ES", {
      day: "numeric",
      month: "short",
      hour: "2-digit",
      minute: "2-digit",
    })
  }

  const isCurrentUser = user?.id === comment.author.id

  return (
    <View style={styles.container}>
      <Image source={{ uri: comment.author.avatar }} style={styles.avatar} />
      <View style={styles.commentContent}>
        <View style={styles.header}>
          <View style={styles.nameContainer}>
            <Text style={styles.username}>{comment.author.username}</Text>
            {comment.author.isAdmin && (
              <View style={styles.adminBadge}>
                <Text style={styles.adminText}>Admin</Text>
              </View>
            )}
            {isCurrentUser && (
              <View style={styles.userBadge}>
                <Text style={styles.userText}>T√∫</Text>
              </View>
            )}
          </View>
          <Text style={styles.date}>{formatDate(comment.createdAt)}</Text>
        </View>
        <Text style={styles.content}>{comment.content}</Text>
      </View>
    </View>
